import React, { useCallback } from 'react';
import PopupWithForm from './PopupWithForm.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';

function AddPlacePopup({ isOpen, onClose, onAddCard }) {

    const [link, setLink] = React.useState('');
    const [description, setDescription] = React.useState('');

    const handleSubmit = useCallback((e) => {
        e.preventDefault();
        onAddCard({
            photoDescription: description,
            link,
        });
    }, [onAddCard, link, description]);

    return (
        <PopupWithForm title="Новое место" name="add-photo" isOpen={isOpen} onClose={onClose} onSubmit={handleSubmit}>
            <label className="popup__input">
                <input className="popup__field popup__field_photo-description" value={description} type="text" placeholder="Название" name="photo-description" minLength="1" maxLength="30" required />
                <span className="popup__field-error"></span>
            </label>

            <label className="popup__input">
                <input className="popup__field popup__field_photo-link" value={link} type="url" placeholder="Ссылка на картинку" name="photo-link" required />
                <span className="popup__field-error"></span>
            </label>

            <button type="submit" className="popup__submit-button" name="Создать" onClick={handleSubmit}>Создать</button>
        </PopupWithForm>
    );

}

export default AddPlacePopup;
