import React from 'react';
import { withRouter } from 'react-router-dom';
import logoPath from '../images/header-logo.png';

function Header(props) {
  console.log(props);
  const locationPathname = props.location.pathname;
  return (
      <header className="header page__header">
        <img className="header__logo" src={logoPath} alt="здесь должен быть логотип, но что-то пошло не так" />
          <a href={(locationPathname === "/sign-in" && "/sign-up") || (locationPathname === "/sign-up" && "/sign-in")} className="header__link">
            {
              locationPathname === "/sign-up" && "Войти" ||
              locationPathname === "/sign-in" && "Регистрация"
            }
          </a>
      </header>
  );
}

export default withRouter(Header);
