import React from 'react';

function Main(props) {
  function handleEditAvatarClick(){
    const popupChangeAvatar = document.querySelector('.popup_type_change-avatar');
    popupChangeAvatar.classList.add('.popup_opened');
  }

  function handleEditProfileClick(){
    const popupEditProfile = document.querySelector('.popup_type_profile');
    popupEditProfile.classList.add('.popup_opened');
  }

  function handleAddPlaceClick(){
    const popupAddPhoto = document.querySelector('.popup_type_add-photo');
    popupAddPhoto.classList.add('.popup_opened');
  }

  return (
      <main className="content-container page__content-conteiner">

        <section className="profile content-container__profile">

          <div className="profile__avatar-overlay">
            <img className="profile__avatar" alt="здесь должно быть фото профиля, но что-то пошло не так" />
            <button type="button" className="profile__avatar-change-button" onClick={handleEditAvatarClick}></button>
          </div>

          <div className="profile__info">
            <div className="profile__name-and-button-container">
              <h1 className="profile__name"></h1>
              <button type="button" className="profile__edit-button" onClick={handleEditProfileClick}></button>
            </div>
            <p className="profile__description"></p>
          </div>

          <button type="button" className="profile__add-button" onClick={handleAddPlaceClick}></button>

        </section>

        <section className="photo-cards">

          <ul className="photo-cards__list">

          </ul>

        </section>

      </main>
  );
}

export default Main;
