import React, { useCallback } from 'react';
import { Link } from 'react-router-dom';
import { ROUTES_MAP } from '../utils/routesMap.js';
import { register } from './Authentication.js';
import AuthenticationPage from './AuthenticationPage.js';
import RegistrationResultsPopup from './RegistrationResultsPopup.js';

function Register({onUpdateAuthForm}) {

    const [formValues, setFormValues] = React.useState({
        email: "",
        password: ""
    });
    const [isRegistrationSuccessfull, setIsRegistrationSuccessfull] = React.useState(false);

  const handleSubmit = useCallback((e) => {
    e.preventDefault();
    register(formValues.email, formValues.password).then((res) => {
      if (res) {
        setIsRegistrationSuccessfull(true);
        <RegistrationResultsPopup ></RegistrationResultsPopup>

      }
      else {
        <RegistrationResultsPopup ></RegistrationResultsPopup>
      }
    })

    /* onUpdateAuthForm({
        email: formValues.email,
        password: formValues.password,
    }); */
  }, [onUpdateAuthForm, formValues]);

    const handleInputChange = useCallback((e) => {
        const { name, value } = e.target;
        setFormValues((prevState) => ({ ...prevState, [name]: value }));
    }, [setFormValues]);

    return (
      <AuthenticationPage title="Регистрация" name="signup" onSubmit={handleSubmit} >
        <label className="authentication__input">
          <input className="authentication__field authentication__field_email" value={formValues.email} onChange={handleInputChange} type="text" name="userEmail" placeholder="Email" />
        </label>

        <label className="authentication__input">
          <input className="authentication__field authentication__field_password" value={formValues.password} onChange={handleInputChange} type="password" name="userPassword" placeholder="Пароль" />
        </label>

        <button className="authentication__submit-button" type="submit" name="Зарегистрироваться">Зарегистрироваться</button>
        <span className="authentication__question-on-registration">Уже зарегистрированы? <Link to={ROUTES_MAP.SIGNIN} className="authentication__login-ref">Войти</Link></span>
      </AuthenticationPage>
    );

}

export default Register;
