import React from 'react';
import { api } from '../utils/Api.js';

function Main(props) {
  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');
  const [userAvatar, setUserAvatar] = React.useState('');
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUserData().
      then((userData) => {
        setUserName(userData.name);
        setUserDescription(userData.about);
        setUserAvatar(userData.avatar);
      }).
      catch((error) => {
        console.log(error);
    });
  }, []);

  React.useEffect(() => {
    api.getInitialCards().
      then((initialCards) => {
        setCards(initialCards);
      }).
      catch((error) => {
        console.log(error);
      });
  }, []);

  ReactDOM.render(
    cards.forEach((card) => {
      return (
        <template className="photo-card-template">
          <li>
            <figure className="photo-card">
              <img className="photo-card__photo" src={card.link} alt="здесь должно быть фото, но что-то пошло не так" />
              <figcaption className="photo-card__caption">
                <h2 className="photo-card__description">{card.name}</h2>
                <div className="photo-card__like-container">
                  <button type="button" className="photo-card__like"></button>
                  <p className="photo-card__likes-amount">{card.likes.length}</p>
                </div>
              </figcaption>
            </figure>
          </li>
        </template>
      );
    }),
    document.getElementById('photo-cards__list')
  );

  return (
    <main className="content-container page__content-container">
        <section className="profile content-container__profile">

          <div className="profile__avatar-overlay">
            <img className="profile__avatar" alt="здесь должно быть фото профиля, но что-то пошло не так" src={userAvatar}/>
            <button type="button" className="profile__avatar-change-button" onClick={props.onEditAvatar}></button>
          </div>

          <div className="profile__info">
            <div className="profile__name-and-button-container">
              <h1 className="profile__name">{userName}</h1>
              <button type="button" className="profile__edit-button" onClick={props.onEditProfile}></button>
            </div>
            <p className="profile__description">{userDescription}</p>
          </div>

          <button type="button" className="profile__add-button" onClick={props.onAddPlace}></button>

        </section>

        <section className="photo-cards">

          <ul className="photo-cards__list" id="photo-cards__list">

          </ul>

        </section>
      </main>
  );
}

export default Main;
